processData:
DOMO.log('metadata.account.access_token: ' + metadata.account.access_token);

httprequest.get(".domo.com/api",data)

httprequest.addHeader('Authorization', 'Bearer ' +  metadata.account.access_token);

var res = httprequest.get('https://graph.facebook.com/v2.10/me?fields=first_name,last_name,about,location,education,work');

DOMO.log('res: ' + res);
var me = JSON.parse(res);

DOMO.log('metadata.reportname: ' +  metadata.report);

if(metadata.report == 'me'){

    datagrid.addColumn('first_name', datagrid.DATA_TYPE_STRING);
    datagrid.addColumn('last_name', datagrid.DATA_TYPE_STRING);
    datagrid.addColumn('locale', datagrid.DATA_TYPE_STRING);
    datagrid.addColumn('about', datagrid.DATA_TYPE_STRING);

    datagrid.addCell(me.first_name);
    datagrid.addCell(me.last_name);
    datagrid.addCell(me.location.name);
    datagrid.addCell(me.about);

    datagrid.endRow();

}
else if(metadata.report == 'me.education'){

    datagrid.addColumn('school', datagrid.DATA_TYPE_STRING);
    datagrid.addColumn('type', datagrid.DATA_TYPE_STRING);
    datagrid.addColumn('year', datagrid.DATA_TYPE_DOUBLE);
    datagrid.addColumn('concentration', datagrid.DATA_TYPE_STRING);

    var data = me.education;

    DOMO.log('data: ' + JSON.stringify(data));

    for(var i = 0; i < data.length; i++) {

        var obj = data[i];

        datagrid.addCell(obj.school.name);
        datagrid.addCell(obj.type);

        var concentration = obj['concentration'];

        DOMO.log('concentration: ' + JSON.stringify(concentration));

        if(concentration != null)datagrid.addCell(concentration[0].name);
        else datagrid.addCell("");

        datagrid.endRow();

    }

}


else if(metadata.report == 'me.work'){

    datagrid.addColumn('employer', datagrid.DATA_TYPE_STRING);
    datagrid.addColumn('location', datagrid.DATA_TYPE_STRING);
    datagrid.addColumn('start_date', datagrid.DATA_TYPE_DATETIME);

    var data = me.work;
    for(var i = 0; i < data.length; i++) {

        var obj = data[i];

        datagrid.addCell(obj.employer.name);
        if(typeof obj.location !== 'undefined') datagrid.addCell(obj.location.name);
        else datagrid.addCell("");
        datagrid.addCell(obj.start_date);

        datagrid.endRow();

    }

}
